function objecttocsv(obj1,obj2){
    var csvstring="Time,Setpoint,Time,Tanklevel,\n"
    for (var key in obj1) {
          if (obj1.hasOwnProperty(key)) {
            data1=JSON.stringify(obj1[key])
            csvstring=csvstring+stringtodata(data1);
            data2=JSON.stringify(obj2[key])
            csvstring=csvstring+stringtodata(data2);
          }
        csvstring=csvstring+"\n";
    }
    return csvstring;
}


function stringtodata(string){
    var finaldata="";
    for(var i in string){
        if(!isNaN(string[i])||string[i]===","){
            finaldata=finaldata+string[i];
        }
    }
    finaldata=finaldata+",";
    return finaldata;
}
